mapper.select:
SELECT count(*)
FROM COMMENTS_B
WHERE CB_PNUM = #{cb_pnum}

SELECT CB_PNUM, CB_NUM, CB_PCNUM, CB_EMAIL, CB_CONTENT, CB_CRDATE, CB_UPDATE, CB_NICKNM
FROM COMMENTS_B
WHERE CB_NUM = #{cb_num}

mapper.update:
UPDATE COMMENTS_B
SET CB_CONTENT = #{cb_content}, CB_UPDATE = SYSDATE
WHERE CB_NUM = #{cb_num} AND CB_EMAIL = #{cb_email}
