mapper.select:
SELECT count(*)
FROM COMMENTS_P
WHERE CP_PNUM = #{cp_pnum}

SELECT CP_PNUM, CP_NUM, CP_PCNUM, CP_EMAIL, CP_CONTENT, CP_CRDATE, CP_UPDATE, CP_NICKNM
FROM COMMENTS_P
WHERE CP_NUM = #{cp_num}

mapper.update:
UPDATE COMMENTS_P
SET CP_CONTENT = #{cp_content}, CP_UPDATE = SYSDATE
WHERE CP_NUM = #{cp_num} AND CP_EMAIL = #{cp_email}
